import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [question, setQuestion] = useState('');
  const [reply, setReply] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const speak = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'ar-SA';
      window.speechSynthesis.speak(utterance);
    } else {
      console.warn('Speech synthesis not supported.');
    }
  };

  const askChef = async () => {
    if (!question.trim()) return; // avoid empty questions

    setLoading(true);
    setReply('');
    setImageUrl('');

    try {
      const response = await axios.post('http://localhost:5000/ask', { question });
      setReply(response.data.reply);
      setImageUrl(response.data.imageUrl);
      speak(response.data.reply);
    } catch (error) {
      setReply('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ÿå ÿ≠ÿßŸàŸÑ ŸÖÿ±ÿ© ÿ´ÿßŸÜŸäÿ©.');
    }

    setLoading(false);
  };

  return (
    <div className="app-container">
      <div className="card">
        <h1 className="title">üë®‚Äçüç≥ ŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ¥ŸäŸÅ ÿßŸÑÿ∞ŸÉŸä</h1>
        <p className="subtitle">ÿßÿ≥ÿ£ŸÑŸÜŸä ÿπŸÜ ŸÉŸäŸÅŸäÿ© ÿ™ÿ≠ÿ∂Ÿäÿ± ÿ£Ÿä ÿ∑ÿ®ŸÇ ÿπÿ±ÿ®Ÿä Ÿàÿ≥ÿ£ÿ¨Ÿäÿ®ŸÉ ÿÆÿ∑Ÿàÿ© ÿ®ÿÆÿ∑Ÿàÿ©!</p>

        <textarea
          className="input-textarea"
          rows={3}
          placeholder="ÿßŸÉÿ™ÿ® ÿ≥ÿ§ÿßŸÑŸÉ ŸÖÿ´ŸÑÿßŸã: ŸÉŸäŸÅ ÿ£ÿπŸÖŸÑ ŸÖÿπÿµŸàÿ®ÿü"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          disabled={loading}
        />

        <button className="submit-button" onClick={askChef} disabled={loading}>
          {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ÿ∂Ÿäÿ±...' : 'ÿßÿ≥ÿ£ŸÑ ÿßŸÑÿ¥ŸäŸÅ'}
        </button>

        {reply && (
          <div className="response-box">
            <p>{reply}</p>
          </div>
        )}

        {imageUrl && (
          <div className="image-box">
            <img src={imageUrl} alt="ÿµŸàÿ±ÿ© ÿßŸÑÿ∑ÿ®ŸÇ" className="dish-image" />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
